Page._content:
#icat-records {
    padding-top: 12px;
}
.icat-records-self,
.icat-records-the-other-side {
    padding: 12px 2rem;
    display: flex;
    clear: both;
}
.icat-records-self {
    float: right;
}
.icat-records-self img,
.icat-records-the-other-side img {
    width: 50px;
    height: 50px;
    border-radius: 6px;
}
/* 头像 */

.icat-records-self-content {
    background: var(--icat-self-content-background-color);
    border-radius: 6px;
    margin-right: -4px;
    padding: 0px 16px;
    margin-left: 66px;
}
.icat-records-the-other-side-content {
    background: var(--icat-the-other-side-content-background-color);
    border-radius: 6px;
    margin-left: 1em;
    padding: 0px 16px;
    margin-right: 66px;
}
.icat-records-self-content p {
    color: #000;
}
.icat-records-the-other-side-content p {
    color: var(--icat-the-other-side-content-text-color);
}
/* 文字内容 */

.icat-records-left-arrowhead {
    position: absolute;
    transform: rotate(90deg);
    margin-left: 48px;
    border-color: transparent;
    border-style: solid;
    border-width: 10px;
    border-top: 8px solid var(--icat-the-other-side-content-background-color);
}
.icat-records-right-arrowhead {
    transform: rotate(-90deg);
    margin-right: -2px;
    border-color: transparent;
    border-style: solid;
    border-width: 10px;
    border-top: 8px solid var(--icat-self-content-background-color);
}
.icat-records-left-arrowhead,
.icat-records-right-arrowhead {
    width: 0;
    height: 0;
    margin-top: 16px;
}
/* 左右箭头 */

.icat-records-chat-time {
    display: flex;
    padding-top: 10px;
}
.icat-records-chat-time span {
    color: var(--icat-chat-time-text-color);
    margin: 0 auto;
    padding: 0 6px;
    background-color: var(--icat-chat-time-background-color);
    border-radius: 4px;
    font-size: 12px;
    font-weight: 400;
}
/* 时间戳 */

summary {
    font-size: 1rem;
    font-weight: 600;
    background-color: #f3f3f3;
    color: #000;
    padding: 1rem;
    margin-bottom: 4px;
    outline: none;
    border-radius: 0.25rem;
    cursor: pointer;
    position: relative;
}
details {
    clear: both;
}
details[open] summary~* {
    animation: sweep .5s ease-in-out;
}
@keyframes sweep {
    0% {
